$(function(){$(".check-btn").click(function(e){e.preventDefault();var t=$(this),c=t.closest(".check-box");if(!c.hasClass("checked")){var a=t.data("toggle").split("|");postJson(t.attr("href"),function(e){200==e.code?(c.toggleClass("checked"),t.html(a[1]+"<em>已连续签到"+e.data.running+"天</em>")):parseAjax(e)})}})});