function bindEdit(){$("#tag-box").select2({ajax:{url:BASE_URI+"tag",data:function(t){return{keywords:t.term,page:t.page||1}},processResults:function(t){return{results:(t=t.data).data.map(function(t){return{id:t.id,text:t.name}}),pagination:{more:t.paging.more}}}}}),$('[data-type="upload_file"]').upload({url:BASE_URI+"post/upload",name:"file",template:"{url}",onafter:function(t,a){return 200==t.code?a.prev("input").val(t.data):302===t.code&&(location.href=t.url),!1}})}