$(function () {
    var footer = $('footer'), diff = $(window).height() - footer.offset().top - footer.height();
    if (diff > 0) {
        footer.css('margin-top', diff + 'px');
    }
    if (!$.cookie('c_t')) {
        $('.dialog-cookie-tip').show();
    }
    $('.dialog-cookie-tip .btn').click(function () {
        $.cookie('c_t', 1);
        $(this).closest('.dialog-cookie-tip').hide();
    });
    if (typeof SUGGESTION_URI === 'undefined' && typeof BASE_URI !== 'undefined') {
        SUGGESTION_URI = BASE_URI + 'suggestion';
    }
    var searchDialog = $('.dialog-search'), refreshSuggestion = function (keywords) {
        var ul = searchDialog.find('.search-suggestion');
        if (typeof SUGGESTION_URI === 'undefined') {
            ul.hide();
            return;
        }
        if (!keywords || keywords.length < 1) {
            ul.html('').hide();
            return;
        }
        $.getJSON(SUGGESTION_URI, {
            keywords: keywords
        }, function (data) {
            if (data.code != 200) {
                return;
            }
            var html = '';
            data.data.forEach(function (item, i) {
                i += 1;
                var url = undefined, title = item;
                if (typeof item === 'object') {
                    url = item.url;
                    title = item.title || item.name;
                }
                if (!url) {
                    html += "<li><span>".concat(i, "</span> ").concat(title, "</li>");
                    return;
                }
                html += "<li data-url=\"".concat(url, "\"><span>").concat(i, "</span> ").concat(title, "</li>");
            });
            ul.html(html).show();
        });
    };
    $('.nav-bar .search-icon').click(function () {
        searchDialog.show();
        searchDialog.find('form input').focus();
    });
    searchDialog.on('click', '.dialog-close', function () {
        searchDialog.hide();
    }).on('keyup', 'form input', function (e) {
        var $this = $(this);
        var keywords = $this.val();
        searchDialog.toggleClass('inputting', keywords.length > 0);
        if (e.key === 'Enter') {
            $this.closest('form').submit();
            return;
        }
        if (e.key !== 'ArrowDown' && e.key !== 'ArrowUp') {
            refreshSuggestion(keywords);
            return;
        }
        var items = searchDialog.find('.search-suggestion li');
        if (items.length < 0) {
            return;
        }
        var i = -1;
        for (var j = 0; j < items.length; j++) {
            var element_1 = $(items[j]);
            if (element_1.hasClass('active')) {
                i = j;
                element_1.removeClass('active');
                break;
            }
        }
        if (e.key === 'ArrowDown') {
            i = i < items.length - 1 ? i + 1 : 0;
        }
        else if (e.key === 'ArrowUp') {
            i = (i < 1 ? items.length : i) - 1;
        }
        var element = items.eq(i);
        element.addClass('active');
        $this.val(element.text().replace(/^\d+/, '').trim());
    }).on('click', '.input-clear', function () {
        $(this).prev('input').val('').trigger('keyup');
    }).on('click', '.search-suggestion li', function () {
        var $this = $(this);
        var url = $this.data('url');
        if (url) {
            window.location.href = url;
            return;
        }
        $this.addClass('active').siblings().removeClass('active');
        searchDialog.find('form input').val($this.text().replace(/^\d+/, '').trim());
        searchDialog.find('form').submit();
    });
});
